mapper.select:
select tt.id, tt.employee_id, tt.state, tt.task_number, tt.task_description, tt.tenant_id, tt.object_version_number, tu.employee_name, tu.employee_number from todo_task tt join todo_user tu on tt.employee_id = tu.id
and tt.task_number = #{taskNumber}
and tt.task_description like #{taskDescriptionLike}
